'------------------------------------------------------------------------------'
def not_f(x):
    vec = []
    for i in range(len(x)-1):
        vec = vec + [x[i+1]]
    return vec

def vec(x):
    a = []
    for i in range(x):
        a = a + [[]]
    return a

def rest(x,y):
    s = x
    if y<=x:
        k = 1
        while k*y<x+1:
            k = k+1
        s = x-(k-1)*y
    return s

def sum_(x,y):
    if len(x) == len(y):
        vec = []
        for i in range(len(x)):
            vec = vec + [x[i] + y[i]]
    return vec
def sub_(x,y):
    if len(x) == len(y):
        vec = []
        for i in range(len(x)):
            # print(vec + [x[i] - y[i]])
            vec = vec + [x[i] - y[i]]
    return vec
def div_(x,y):
    if len(x) == len(y):
        vec = []
        for i in range(len(x)):
            vec = vec + [x[i]/y[i]]
    return vec
def prod_(x,y):
    if len(x) == len(y):
        vec = []
        for i in range(len(x)):
            vec = vec + [x[i]*y[i]]
    return vec
def binar(x,y):
    c = 0
    while y**c<=x:
        c = c+1
    c = c-1
    # print(c)
    # print(y**c)
    vect = vec(c+1)
    if x == 0: 
        vect = [0]
    else: 
        for i in range(c+1):
            vect[i] = int(x/(y**(c-i)))
            x = x-(vect[i])*(y**(c-i))
    return vect


def search(k,x):
    vr = [[0]]
    l = len(x)
    for i in range(len(k)):
        if k[i:i+l] == x:
            vr[len(vr)-1] = vr[len(vr)-1] + [i-1]
            vr = vr + [[i+l+1]]
    return vr

